document.addEventListener('DOMContentLoaded', () => {
    // Type Selection
    const typeButtons = document.querySelectorAll('.type-btn');
    typeButtons.forEach(btn => {
        btn.addEventListener('click', (e) => {
            typeButtons.forEach(b => b.classList.remove('active'));
            e.target.classList.add('active');
            selectedType = e.target.dataset.type;
        });
    });

    // File Upload Area Click
    const uploadArea = document.getElementById('upload-area');
    const imageUpload = document.getElementById('image-upload');

    // Drag and Drop implementation
    uploadArea.addEventListener('dragover', (e) => {
        e.preventDefault();
        uploadArea.style.borderColor = 'var(--secondary)';
        uploadArea.style.background = 'rgba(157, 78, 221, 0.2)';
    });

    uploadArea.addEventListener('dragleave', () => {
        uploadArea.style.borderColor = 'var(--primary)';
        uploadArea.style.background = 'rgba(0,0,0,0.2)';
    });

    uploadArea.addEventListener('drop', (e) => {
        e.preventDefault();
        uploadArea.style.borderColor = 'var(--primary)';
        uploadArea.style.background = 'rgba(0,0,0,0.2)';

        if (e.dataTransfer.files && e.dataTransfer.files[0]) {
            handleFile(e.dataTransfer.files[0]);
        }
    });

    imageUpload.addEventListener('change', function () {
        if (this.files && this.files[0]) {
            handleFile(this.files[0]);
        }
    });

    // Retry Button
    document.getElementById('btn-retry').addEventListener('click', () => {
        imageUpload.value = ''; // Reset input
        switchView(UPLOAD_VIEW);
    });

    // Share Button
    document.getElementById('btn-share').addEventListener('click', () => {
        if (navigator.share) {
            navigator.share({
                title: 'AI 운명 스캐너 - 내 운명 분석 결과!',
                text: '나의 손금, 족상 분석 결과를 확인해보세요. 무료로 AI가 분석해줍니다!',
                url: window.location.href,
            })
                .then(() => console.log('성공적으로 공유되었습니다.'))
                .catch((error) => console.log('공유 에러:', error));
        } else {
            // Fallback for browsers that don't support navigator.share
            navigator.clipboard.writeText(window.location.href).then(() => {
                alert('웹사이트 링크가 복사되었습니다! 친구들에게 붙여넣기로 공유해보세요.');
            });
        }
    });
});

function handleFile(file) {
    if (!file.type.startsWith('image/')) {
        alert('이미지 파일만 업로드 가능합니다.');
        return;
    }

    // Load file locally using FileReader (No Backend)
    const reader = new FileReader();
    reader.onload = function (e) {
        const imgElement = document.getElementById('preview-image');
        imgElement.src = e.target.result;

        // Wait for image to be fully loaded into the DOM element before AI validation
        imgElement.onload = async () => {
            // Bypass validation if MediaPipe failed to load (e.g. strict CORS, offline)
            if (isModelsLoaded === 'failedFallback') {
                document.getElementById('image-upload').value = '';
                startScanning();
                return;
            }

            if (!isModelsLoaded) {
                alert("AI 객체 분석 모델 셋업 중입니다. 1~2초 후 액션을 다시 시도해주세요.");
                document.getElementById('image-upload').value = ''; // Reset input so user can try same file again
                return;
            }

            // Show a tiny 'checking' indicator
            const uploadBtn = document.querySelector('.btn-primary');
            const originalText = uploadBtn.innerText;
            uploadBtn.innerText = "객체 판단 중...";
            uploadBtn.disabled = true;

            let isValid = true;
            let errorMessage = "";

            if (selectedType === '손금') {
                const handResult = handLandmarker.detect(imgElement);
                const faceResult = faceLandmarker.detect(imgElement);

                // Reject faces
                if (faceResult.faceLandmarks.length > 0) {
                    isValid = false;
                    errorMessage = "얼굴이 감지되었습니다! 손바닥 사진을 둥글게 펼쳐서 올려주세요.";
                } else {
                    // Check for a hand, with a strict confidence score to reject feet
                    let isDefiniteHand = false;
                    if (handResult.handednesses.length > 0) {
                        if (handResult.handednesses[0][0].score > 0.85) {
                            isDefiniteHand = true;
                        }
                    }

                    if (!isDefiniteHand) {
                        isValid = false;
                        errorMessage = "손이 정확히 감지되지 않았습니다. 손바닥 정면이 잘 보이는 사진인지, 혹은 발 사진은 아닌지 확인해주세요!";
                    }
                }
            } else if (selectedType === '족상') {
                const faceResult = faceLandmarker.detect(imgElement);
                if (faceResult.faceLandmarks.length === 0) {
                    isValid = false;
                    errorMessage = "얼굴이 감지되지 않았습니다. 관상을 볼 수 있게 얼굴 사진을 올려주세요!";
                }
            } else if (selectedType === '족금') {
                const handResultForFoot = handLandmarker.detect(imgElement);
                const faceResultForFoot = faceLandmarker.detect(imgElement);

                // MediaPipe Hand landmarker is aggressive and sometimes thinks toes/foot shapes are hands.
                // We add a strict confidence threshold (e.g. > 0.8) to only reject obvious hands.
                let isDefiniteHand = false;
                if (handResultForFoot.handednesses.length > 0) {
                    // handednesses[0][0].score contains the confidence score
                    if (handResultForFoot.handednesses[0][0].score > 0.85) {
                        isDefiniteHand = true;
                    }
                }

                if (isDefiniteHand) {
                    isValid = false;
                    errorMessage = "손이 강하게 감지되었습니다. 족금을 보시려면 발바닥(발) 사진을 올려주세요!";
                } else if (faceResultForFoot.faceLandmarks.length > 0) {
                    isValid = false;
                    errorMessage = "얼굴이 감지되었습니다. 족금을 보시려면 발바닥(발) 사진을 올려주세요!";
                }
            }

            uploadBtn.innerText = originalText;
            uploadBtn.disabled = false;

            if (!isValid) {
                alert("🚨 AI 분석 경고: " + errorMessage);
                document.getElementById('image-upload').value = ''; // Reset input
                return; // Stop execution, don't go to scanning view
            }

            // If valid, clear input and proceed to the fake scanner
            document.getElementById('image-upload').value = '';
            startScanning();
        };
    };
    reader.readAsDataURL(file);
}

function switchView(viewId) {
    document.querySelectorAll('.view').forEach(v => {
        if (v.id === viewId) {
            v.style.display = 'block'; // Ensure it's block before adding active
            // Small timeout to allow transition
            setTimeout(() => {
                v.classList.add('active');
                v.classList.remove('hidden');
            }, 10);
        } else {
            v.classList.remove('active');
            // Hide it completely after fade out
            setTimeout(() => {
                v.classList.add('hidden');
                v.style.display = 'none';
            }, 500);
        }
    });
}

function startScanning() {
    // Hide others immediately
    document.querySelectorAll('.view').forEach(v => {
        v.classList.remove('active');
        v.classList.add('hidden');
        v.style.display = 'none';
    });

    // Show scanning view
    const scanningView = document.getElementById(SCANNING_VIEW);
    scanningView.style.display = 'block';
    setTimeout(() => {
        scanningView.classList.add('active');
        scanningView.classList.remove('hidden');
    }, 10);

    // Fake AI Loading sequence (Optimized for 5 second Ad dwell time)
    const statusText = document.getElementById('scan-status');
    const progressBar = document.getElementById('progress-bar');

    progressBar.style.width = '0%';

    // Adjust text according to type
    let targetWord = selectedType; // 손금, 족상, 족금

    const sequence = [
        { time: 0, text: `> 업로드된 [${targetWord}] 이미지를 전처리하고 있습니다...`, p: 10 },
        { time: 1000, text: "> 딥러닝 신경망 활성화. 주요 생체선(Line) 및 특징 추출 중...", p: 35 },
        { time: 2500, text: "> 수십만 건의 빅데이터와 패턴 대조 분석 진행 중...", p: 65 },
        { time: 4000, text: "> 운명적 알고리즘으로 최종 결과 산출 중...", p: 90 },
        { time: 5000, text: "> 분석 완료! 결과를 렌더링합니다.", p: 100 }
    ];

    sequence.forEach((step, index) => {
        setTimeout(() => {
            statusText.innerText = step.text;
            progressBar.style.width = `${step.p}%`;

            if (index === sequence.length - 1) {
                setTimeout(showResults, 800);
            }
        }, step.time);
    });
}

function showResults() {
    // Pick random fortune completely locally (0 Cost API)
    const dataList = fortuneData[selectedType] || fortuneData['손금'];
    const randomIdx = Math.floor(Math.random() * dataList.length);
    const result = dataList[randomIdx];

    document.getElementById('result-wealth').innerText = result.wealth;
    document.getElementById('result-love').innerText = result.love;
    document.getElementById('result-general').innerText = result.general;

    switchView(RESULT_VIEW);
}
